import React, { Component } from "react";
import { Icon } from "semantic-ui-react";
import { getQuotes } from "../api";
import Quotes from "./Quotes";

class Thisforthat extends Component {
  constructor(props) {
    super(props);

    this.state = {
      quotes: [],
      click: false
    };
  }

  componentDidMount = async () => {
    getQuotes()
      .then(res => this.setState({ quotes: res.data }))
      .catch(err => console.error("Error", err));
  };

  getNewQuotes = async e => {
    e.preventDefault();
    getQuotes()
      .then(res => this.setState({ click: true, quotes: res.data }))
      .catch(err => console.error("Error", err));
  };

  render() {
    console.log(this.state);
    return (
      <div>
        <Icon.Group size="large">
          <Icon name="twitter" />
          <Icon corner name="add" />
        </Icon.Group>
        <Quotes quotes={this.state.quotes} />
        <button onClick={this.getNewQuotes}>
          <Icon name="user" />
        </button>
        <Icon name="user" />
      </div>
    );
  }
}

export default Thisforthat;
