import React, { Component } from "react";
import { getQuotes } from "../api";
import Quotes from "./Quotes";
import { Icon, Button } from "semantic-ui-react";
import "semantic-ui-css/semantic.min.css";

class Thisforthat extends Component {
  constructor(props) {
    super(props);

    this.state = {
      quotes: []
    };
  }

  componentDidMount = async () => {
    getQuotes()
      .then(res => this.setState({ quotes: res.data }))
      .catch(err => console.error("Error", err));
  };

  getNewQuotes = async e => {
    e.preventDefault();
    getQuotes()
      .then(res => this.setState({ quotes: res.data }))
      .catch(err => console.error("Error", err));
  };

  render() {
    console.log(this.state);
    const { that } = this.state.quotes;
    return (
      <div>
        <Quotes this={this.state.quotes.this} that={that} />
        <Button animated onClick={this.getNewQuotes}>
          <Button.Content visible>
            <Icon name="redo" />
          </Button.Content>
          <Button.Content hidden>Refresh</Button.Content>
        </Button>
        <Button color="twitter">
          <Icon name="twitter" /> Tweet
        </Button>
      </div>
    );
  }
}

export default Thisforthat;
