import React, { Component } from "react";
import axios from "axios";
import { Icon } from "antd";

class Thisforthat extends Component {
  constructor(props) {
    super(props);

    this.state = {
      quotes: [],
      newQuotes: [],
      click: false
    };
  }

  async componentDidMount() {
    const proxyUrl = "https://cors-anywhere.herokuapp.com/";
    const apiUrl = "http://itsthisforthat.com/api.php?json";

    // https://stackoverflow.com/questions/43871637/no-access-control-allow-origin-header-is-present-on-the-requested-resource-whe
    await axios
      .get(`${proxyUrl + apiUrl}`)
      .then(res => this.setState({ quotes: res.data }))
      .catch(err => console.error("Error", err));
  }

  async getNewQuotes(e) {
    e.preventDefault();
    const proxyUrl = "https://cors-anywhere.herokuapp.com/";
    const apiUrl = "http://itsthisforthat.com/api.php?json";
    await axios
      .get(`${proxyUrl + apiUrl}`)
      // .then(res => this.setState({ click: true, newQuotes: res.data }))
      .then(res => {
        this.setState({ newQuotes: res.data });
        console.log(res.data);
      })
      .catch(err => console.error("Error", err));
  }

  render() {
    console.log(this.state);
    return (
      <div>
        <h2>{this.state.quotes.this}</h2>
        for
        <h2>{this.state.quotes.that}</h2>
        <button onClick={this.getNewQuotes}>
          <Icon type="redo" />
        </button>
      </div>
    );
  }
}

export default Thisforthat;
